sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {
    const sysid = api.context.props.sysid;

    console.log("CHECK FOR SYSID FROM NAV BAR");

    if (sysid == '-99') {
        api.setState('uidQuery', `user=${api.context.session.user.sys_id}`);
    }
}
