sys_ux_client_script.script:
function handler({ api, event, helpers, imports }) {
  var listMenuIDs = api.context.props.listMenuIDs;
  const currList = api.state.currList;

  // Replacing hyphen for underscore for already completed pages.
  var targetID = event.payload.targetSysID;

  if (targetID != -1) {
    targetID = targetID.replaceAll("-", "_");
  }

  // var listMenuIDs = {
  //   my_sales_orders: "1dd1665c07b2301029a8f0269c1ed0a2",
  //   my_drafts: "bad064c907b2301029a8f0269c1ed01f",
  //   all_sales_orders: "cba4a0e107a2701029a8f0269c1ed01e",
  //   my_authorization_requests: "16ccf4b00722301029a8f0269c1ed0d2",
  // };

  const newURLs = {
    salesOrder_existing: { page: "sales-order-confirm", salesorder: targetID },
    salesOrder_new: { page: "sales-order-new", sOrder: targetID },
    auth_req: { page: "auth-request" },
    auth_req_exist: { page: "auth-request-existing", sysid: targetID },
  };

  const listIds = {
    salesOrder: [
      listMenuIDs.my_sales_orders,
      listMenuIDs.my_drafts,
      listMenuIDs.all_sales_orders,
    ],
    authReq: [listMenuIDs.my_authorization_requests],
  };

  // check if selected list is sales order
  const isSO = listIds.salesOrder.some((so) => so == currList);

  if (isSO) {
    if (targetID == -1) {
      helpers.navigate.to(newURLs.salesOrder_new.page, { sOrder: targetID });
      return;
    }
    helpers.navigate.to(newURLs.salesOrder_existing.page, {
      salesorder: targetID,
    });
    return;
  }

  // check if selected list is auth request
  const isAuth = listIds.authReq.some((a) => a == currList);

  if (isAuth) {
    if (targetID == -1) {
      helpers.navigate.to(newURLs.auth_req.page);
      return;
    }
    // NAVIGATE TO EXISTING AUTH PAGE
    helpers.navigate.to(newURLs.auth_req_exist.page, { sysid: targetID });
  }
}
